import React from 'react';
import ToDo from './ToDo';


const ToDoList = ({tasks, match}) => {
    let filtredTasks;

    switch (match.params.filter){
        case 'completed': 
            filtredTasks = task.filter(task =>task.completed)
            break;
        default :
            filtredTasks = tasks
    }


    return (
            <>
            <h1 className="m-3">Liste de t√¢ches</h1>
            <ul className="list-group m-3">
                {
                    filtredTasks.map((task) => <ToDo task={task} key={task.id}/>)
                }
            </ul>
            </>
    )
}

export default ToDoList;
